import React, { useState, useMemo } from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Plus, Search, Filter, Grid3X3, List, Loader2 } from 'lucide-react';
import SampleCard from '../components/samples/SampleCard';
import { useTranslation } from '../components/i18n/translations';

export default function Samples() {
  const { t } = useTranslation();
  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [brandFilter, setBrandFilter] = useState('all');
  const [locationFilter, setLocationFilter] = useState('all');
  const [fireSaleOnly, setFireSaleOnly] = useState(false);
  const [viewMode, setViewMode] = useState('grid');

  const { data: samples = [], isLoading } = useQuery({
    queryKey: ['samples'],
    queryFn: () => base44.entities.Sample.list('-created_date')
  });

  // Get unique brands and locations for filters
  const brands = useMemo(() => {
    const uniqueBrands = [...new Set(samples.map(s => s.brand).filter(Boolean))];
    return uniqueBrands.sort();
  }, [samples]);

  const locations = useMemo(() => {
    const uniqueLocations = [...new Set(samples.map(s => s.location).filter(Boolean))];
    return uniqueLocations.sort();
  }, [samples]);

  // Filter samples
  const filteredSamples = useMemo(() => {
    return samples.filter(sample => {
      const matchesSearch = !search || 
        sample.name?.toLowerCase().includes(search.toLowerCase()) ||
        sample.brand?.toLowerCase().includes(search.toLowerCase()) ||
        sample.qr_code?.toLowerCase().includes(search.toLowerCase());

      const matchesStatus = statusFilter === 'all' || sample.status === statusFilter;
      const matchesBrand = brandFilter === 'all' || sample.brand === brandFilter;
      const matchesLocation = locationFilter === 'all' || sample.location === locationFilter;
      const matchesFireSale = !fireSaleOnly || sample.fire_sale;

      return matchesSearch && matchesStatus && matchesBrand && matchesLocation && matchesFireSale;
    });
  }, [samples, search, statusFilter, brandFilter, locationFilter, fireSaleOnly]);

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h1 className="text-2xl font-bold text-slate-900">{t('sample.titlePlural')}</h1>
              <p className="text-sm text-slate-500 mt-1">
                {filteredSamples.length} of {samples.length} samples
              </p>
            </div>
            <Link to={createPageUrl('SampleCreate')}>
              <Button className="bg-slate-900 hover:bg-slate-800">
                <Plus className="w-4 h-4 mr-2" />
                {t('sample.createNew')}
              </Button>
            </Link>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Filters */}
        <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-4 mb-6">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
              <Input
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                placeholder={t('filters.searchPlaceholder')}
                className="pl-10"
              />
            </div>

            {/* Filter dropdowns */}
            <div className="flex flex-wrap gap-3">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-36">
                  <SelectValue placeholder={t('filters.allStatuses')} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">{t('filters.allStatuses')}</SelectItem>
                  <SelectItem value="available">{t('status.available')}</SelectItem>
                  <SelectItem value="checked_out">{t('status.checked_out')}</SelectItem>
                  <SelectItem value="reserved">{t('status.reserved')}</SelectItem>
                  <SelectItem value="discontinued">{t('status.discontinued')}</SelectItem>
                </SelectContent>
              </Select>

              <Select value={brandFilter} onValueChange={setBrandFilter}>
                <SelectTrigger className="w-36">
                  <SelectValue placeholder={t('filters.allBrands')} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">{t('filters.allBrands')}</SelectItem>
                  {brands.map(brand => (
                    <SelectItem key={brand} value={brand}>{brand}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={locationFilter} onValueChange={setLocationFilter}>
                <SelectTrigger className="w-36">
                  <SelectValue placeholder={t('filters.allLocations')} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">{t('filters.allLocations')}</SelectItem>
                  {locations.map(loc => (
                    <SelectItem key={loc} value={loc}>{loc}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <div className="flex items-center gap-2 px-3 py-2 bg-orange-50 rounded-lg border border-orange-200">
                <Switch
                  id="fire-sale"
                  checked={fireSaleOnly}
                  onCheckedChange={setFireSaleOnly}
                />
                <Label htmlFor="fire-sale" className="text-sm font-medium text-orange-700 cursor-pointer">
                  ðŸ”¥ {t('filters.fireSaleOnly')}
                </Label>
              </div>

              {/* View toggle */}
              <div className="flex border border-slate-200 rounded-lg overflow-hidden">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 ${viewMode === 'grid' ? 'bg-slate-100' : 'bg-white hover:bg-slate-50'}`}
                >
                  <Grid3X3 className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 ${viewMode === 'list' ? 'bg-slate-100' : 'bg-white hover:bg-slate-50'}`}
                >
                  <List className="w-4 h-4" />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Sample Grid/List */}
        {isLoading ? (
          <div className="flex items-center justify-center py-20">
            <Loader2 className="w-8 h-8 animate-spin text-slate-400" />
          </div>
        ) : filteredSamples.length === 0 ? (
          <div className="text-center py-20">
            <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Filter className="w-8 h-8 text-slate-400" />
            </div>
            <h3 className="text-lg font-medium text-slate-900 mb-1">{t('messages.noResults')}</h3>
            <p className="text-slate-500">Try adjusting your filters</p>
          </div>
        ) : viewMode === 'grid' ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
            {filteredSamples.map(sample => (
              <SampleCard key={sample.id} sample={sample} />
            ))}
          </div>
        ) : (
          <div className="space-y-2">
            {filteredSamples.map(sample => (
              <Link key={sample.id} to={createPageUrl(`SampleDetails?id=${sample.id}`)}>
                <SampleCard sample={sample} compact />
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

pages.SampleDetails:
import React from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Link, useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  ArrowLeft, Edit, Trash2, Package, MapPin, Calendar, 
  User, ExternalLink, Loader2, AlertTriangle
} from 'lucide-react';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import StatusBadge from '../components/ui/StatusBadge';
import FireSaleBadge from '../components/ui/FireSaleBadge';
import QRCodeDisplay from '../components/ui/QRCodeDisplay';
import PriceDisplay from '../components/ui/PriceDisplay';
import { useTranslation } from '../components/i18n/translations';

export default function SampleDetails() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const urlParams = new URLSearchParams(window.location.search);
  const sampleId = urlParams.get('id');

  const { data: sample, isLoading } = useQuery({
    queryKey: ['sample', sampleId],
    queryFn: async () => {
      const samples = await base44.entities.Sample.filter({ id: sampleId });
      return samples[0];
    },
    enabled: !!sampleId
  });

  const { data: bundle } = useQuery({
    queryKey: ['bundle', sample?.bundle_id],
    queryFn: async () => {
      if (!sample?.bundle_id) return null;
      const bundles = await base44.entities.Bundle.filter({ id: sample.bundle_id });
      return bundles[0];
    },
    enabled: !!sample?.bundle_id
  });

  const { data: transactions = [] } = useQuery({
    queryKey: ['transactions', sampleId],
    queryFn: () => base44.entities.InventoryTransaction.filter({ sample_id: sampleId }, '-created_date', 10),
    enabled: !!sampleId
  });

  const deleteMutation = useMutation({
    mutationFn: () => base44.entities.Sample.delete(sampleId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['samples'] });
      navigate(createPageUrl('Samples'));
    }
  });

  const defaultImage = 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=600&h=600&fit=crop';

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-slate-400" />
      </div>
    );
  }

  if (!sample) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="w-12 h-12 text-amber-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-slate-900 mb-2">{t('sample.notFound')}</h2>
          <Link to={createPageUrl('Samples')}>
            <Button variant="outline">{t('actions.back')}</Button>
          </Link>
        </div>
      </div>
    );
  }

  const formatDate = (dateStr) => {
    if (!dateStr) return 'â€”';
    return new Date(dateStr).toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: 'numeric',
      minute: '2-digit'
    });
  };

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <Link to={createPageUrl('Samples')} className="flex items-center gap-2 text-slate-600 hover:text-slate-900">
              <ArrowLeft className="w-4 h-4" />
              <span>{t('sample.titlePlural')}</span>
            </Link>
            <div className="flex items-center gap-2">
              <Link to={createPageUrl(`SampleEdit?id=${sample.id}`)}>
                <Button variant="outline" size="sm">
                  <Edit className="w-4 h-4 mr-2" />
                  {t('actions.edit')}
                </Button>
              </Link>
              <AlertDialog>
                <AlertDialogTrigger asChild>
                  <Button variant="outline" size="sm" className="text-red-600 hover:text-red-700 hover:bg-red-50">
                    <Trash2 className="w-4 h-4 mr-2" />
                    {t('actions.delete')}
                  </Button>
                </AlertDialogTrigger>
                <AlertDialogContent>
                  <AlertDialogHeader>
                    <AlertDialogTitle>{t('sample.deleteSample')}</AlertDialogTitle>
                    <AlertDialogDescription>
                      {t('sample.deleteConfirm')}
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <AlertDialogFooter>
                    <AlertDialogCancel>{t('actions.cancel')}</AlertDialogCancel>
                    <AlertDialogAction 
                      onClick={() => deleteMutation.mutate()}
                      className="bg-red-600 hover:bg-red-700"
                    >
                      {t('actions.delete')}
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid gap-6 lg:grid-cols-3">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Hero Card */}
            <Card className="overflow-hidden">
              <div className="md:flex">
                <div className="md:w-1/3">
                  <div className="aspect-square relative">
                    <img 
                      src={sample.picture_url || defaultImage}
                      alt={sample.name}
                      className="w-full h-full object-cover"
                    />
                    <div className="absolute top-3 left-3 flex flex-wrap gap-2">
                      <StatusBadge status={sample.status} />
                      {sample.fire_sale && <FireSaleBadge />}
                    </div>
                  </div>
                </div>
                <CardContent className="p-6 md:w-2/3">
                  <h1 className="text-2xl font-bold text-slate-900 mb-1">{sample.name}</h1>
                  <p className="text-lg text-slate-500 mb-4">{sample.brand}</p>

                  <div className="mb-4">
                    <QRCodeDisplay code={sample.qr_code} />
                  </div>

                  <div className="flex flex-wrap gap-4 text-sm">
                    {sample.location && (
                      <div className="flex items-center gap-1 text-slate-600">
                        <MapPin className="w-4 h-4" />
                        {sample.location}
                      </div>
                    )}
                    {bundle && (
                      <Link 
                        to={createPageUrl(`BundleDetails?id=${bundle.id}`)}
                        className="flex items-center gap-1 text-indigo-600 hover:text-indigo-800"
                      >
                        <Package className="w-4 h-4" />
                        {bundle.name}
                      </Link>
                    )}
                  </div>
                </CardContent>
              </div>
            </Card>

            {/* Pricing */}
            <Card>
              <CardHeader>
                <CardTitle>Pricing</CardTitle>
              </CardHeader>
              <CardContent>
                <PriceDisplay 
                  currentPrice={sample.current_price}
                  bestPrice={sample.best_price}
                  bestPriceSource={sample.best_price_source}
                  lastChecked={sample.last_price_checked_at}
                />

                {sample.tiktok_affiliate_link && (
                  <a 
                    href={sample.tiktok_affiliate_link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center gap-2 mt-4 px-4 py-2 bg-slate-900 text-white rounded-lg hover:bg-slate-800 transition-colors"
                  >
                    TikTok Affiliate Link <ExternalLink className="w-4 h-4" />
                  </a>
                )}
              </CardContent>
            </Card>

            {/* Notes */}
            {sample.notes && (
              <Card>
                <CardHeader>
                  <CardTitle>{t('sample.notes')}</CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-slate-600 whitespace-pre-wrap">{sample.notes}</p>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Checkout Status */}
            <Card>
              <CardHeader>
                <CardTitle>Checkout Status</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {sample.checked_out_to && (
                  <div className="flex items-center gap-3">
                    <User className="w-4 h-4 text-slate-400" />
                    <div>
                      <p className="text-sm text-slate-500">{t('sample.checkedOutTo')}</p>
                      <p className="font-medium">{sample.checked_out_to}</p>
                    </div>
                  </div>
                )}
                {sample.checked_out_at && (
                  <div className="flex items-center gap-3">
                    <Calendar className="w-4 h-4 text-slate-400" />
                    <div>
                      <p className="text-sm text-slate-500">{t('sample.checkedOutAt')}</p>
                      <p className="font-medium">{formatDate(sample.checked_out_at)}</p>
                    </div>
                  </div>
                )}
                {sample.checked_in_at && (
                  <div className="flex items-center gap-3">
                    <Calendar className="w-4 h-4 text-slate-400" />
                    <div>
                      <p className="text-sm text-slate-500">{t('sample.checkedInAt')}</p>
                      <p className="font-medium">{formatDate(sample.checked_in_at)}</p>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Recent Transactions */}
            <Card>
              <CardHeader>
                <CardTitle>Recent Activity</CardTitle>
              </CardHeader>
              <CardContent>
                {transactions.length === 0 ? (
                  <p className="text-sm text-slate-400 text-center py-4">No activity yet</p>
                ) : (
                  <div className="space-y-3">
                    {transactions.map(tx => (
                      <div key={tx.id} className="flex items-start gap-3 text-sm">
                        <div className={`
                          w-2 h-2 rounded-full mt-1.5 flex-shrink-0
                          ${tx.action === 'checkout' ? 'bg-amber-500' : 
                            tx.action === 'checkin' ? 'bg-emerald-500' : 'bg-blue-500'}
                        `} />
                        <div>
                          <p className="font-medium capitalize">{tx.action}</p>
                          <p className="text-slate-500">
                            {formatDate(tx.created_date)}
                            {tx.operator && ` by ${tx.operator}`}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

pages.SampleCreate:
import React from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Link, useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { ArrowLeft } from 'lucide-react';
import SampleForm from '../components/samples/SampleForm';
import { useTranslation } from '../components/i18n/translations';

export default function SampleCreate() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const queryClient = useQueryClient();

  const { data: bundles = [] } = useQuery({
    queryKey: ['bundles'],
    queryFn: () => base44.entities.Bundle.list()
  });

  const createMutation = useMutation({
    mutationFn: (data) => base44.entities.Sample.create(data),
    onSuccess: (newSample) => {
      queryClient.invalidateQueries({ queryKey: ['samples'] });
      navigate(createPageUrl(`SampleDetails?id=${newSample.id}`));
    }
  });

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <Link to={createPageUrl('Samples')} className="flex items-center gap-2 text-slate-600 hover:text-slate-900">
            <ArrowLeft className="w-4 h-4" />
            <span>{t('sample.titlePlural')}</span>
          </Link>
          <h1 className="text-2xl font-bold text-slate-900 mt-4">{t('sample.createNew')}</h1>
        </div>
      </div>

      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <SampleForm 
          bundles={bundles}
          onSave={(data) => createMutation.mutate(data)}
          onCancel={() => navigate(createPageUrl('Samples'))}
        />
      </div>
    </div>
  );
}

pages.SampleEdit:
import React from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Link, useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { ArrowLeft, Loader2, AlertTriangle } from 'lucide-react';
import { Button } from "@/components/ui/button";
import SampleForm from '../components/samples/SampleForm';
import { useTranslation } from '../components/i18n/translations';

export default function SampleEdit() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const urlParams = new URLSearchParams(window.location.search);
  const sampleId = urlParams.get('id');

  const { data: sample, isLoading } = useQuery({
    queryKey: ['sample', sampleId],
    queryFn: async () => {
      const samples = await base44.entities.Sample.filter({ id: sampleId });
      return samples[0];
    },
    enabled: !!sampleId
  });

  const { data: bundles = [] } = useQuery({
    queryKey: ['bundles'],
    queryFn: () => base44.entities.Bundle.list()
  });

  const updateMutation = useMutation({
    mutationFn: (data) => base44.entities.Sample.update(sampleId, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['samples'] });
      queryClient.invalidateQueries({ queryKey: ['sample', sampleId] });
      navigate(createPageUrl(`SampleDetails?id=${sampleId}`));
    }
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-slate-400" />
      </div>
    );
  }

  if (!sample) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="w-12 h-12 text-amber-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-slate-900 mb-2">{t('sample.notFound')}</h2>
          <Link to={createPageUrl('Samples')}>
            <Button variant="outline">{t('actions.back')}</Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <Link to={createPageUrl(`SampleDetails?id=${sampleId}`)} className="flex items-center gap-2 text-slate-600 hover:text-slate-900">
            <ArrowLeft className="w-4 h-4" />
            <span>Back to Sample</span>
          </Link>
          <h1 className="text-2xl font-bold text-slate-900 mt-4">{t('sample.editSample')}</h1>
        </div>
      </div>

      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <SampleForm 
          sample={sample}
          bundles={bundles}
          onSave={(data) => updateMutation.mutate(data)}
          onCancel={() => navigate(createPageUrl(`SampleDetails?id=${sampleId}`))}
        />
      </div>
    </div>
  );
}

pages.Bundles:
import React, { useState, useMemo } from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Plus, Search, Loader2, Package } from 'lucide-react';
import BundleCard from '../components/bundles/BundleCard';
import { useTranslation } from '../components/i18n/translations';

export default function Bundles() {
  const { t } = useTranslation();
  const [search, setSearch] = useState('');

  const { data: bundles = [], isLoading } = useQuery({
    queryKey: ['bundles'],
    queryFn: () => base44.entities.Bundle.list('-created_date')
  });

  const { data: samples = [] } = useQuery({
    queryKey: ['samples'],
    queryFn: () => base44.entities.Sample.list()
  });

  // Get sample counts per bundle
  const sampleCounts = useMemo(() => {
    const counts = {};
    samples.forEach(sample => {
      if (sample.bundle_id) {
        counts[sample.bundle_id] = (counts[sample.bundle_id] || 0) + 1;
      }
    });
    return counts;
  }, [samples]);

  // Filter bundles
  const filteredBundles = useMemo(() => {
    if (!search) return bundles;
    return bundles.filter(bundle =>
      bundle.name?.toLowerCase().includes(search.toLowerCase()) ||
      bundle.qr_code?.toLowerCase().includes(search.toLowerCase()) ||
      bundle.location?.toLowerCase().includes(search.toLowerCase())
    );
  }, [bundles, search]);

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h1 className="text-2xl font-bold text-slate-900">{t('bundle.titlePlural')}</h1>
              <p className="text-sm text-slate-500 mt-1">
                {filteredBundles.length} bundles
              </p>
            </div>
            <Link to={createPageUrl('BundleCreate')}>
              <Button className="bg-slate-900 hover:bg-slate-800">
                <Plus className="w-4 h-4 mr-2" />
                {t('bundle.createNew')}
              </Button>
            </Link>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Search */}
        <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-4 mb-6">
          <div className="relative max-w-md">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
            <Input
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search bundles..."
              className="pl-10"
            />
          </div>
        </div>

        {/* Bundle Grid */}
        {isLoading ? (
          <div className="flex items-center justify-center py-20">
            <Loader2 className="w-8 h-8 animate-spin text-slate-400" />
          </div>
        ) : filteredBundles.length === 0 ? (
          <div className="text-center py-20">
            <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Package className="w-8 h-8 text-slate-400" />
            </div>
            <h3 className="text-lg font-medium text-slate-900 mb-1">
              {bundles.length === 0 ? 'No bundles yet' : t('messages.noResults')}
            </h3>
            <p className="text-slate-500">
              {bundles.length === 0 ? 'Create your first bundle to group samples together' : 'Try adjusting your search'}
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredBundles.map(bundle => (
              <BundleCard 
                key={bundle.id} 
                bundle={bundle} 
                sampleCount={sampleCounts[bundle.id] || 0}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

pages.BundleDetails:
import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Link, useNavigate } from 'react-router-dom';
import { createPageUrl } from '@/utils';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  ArrowLeft, Edit, Trash2, Package, MapPin, Plus, X,
  Loader2, AlertTriangle
} from 'lucide-react';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import QRCodeDisplay from '../components/ui/QRCodeDisplay';
import SampleCard from '../components/samples/SampleCard';
import { useTranslation } from '../components/i18n/translations';

export default function BundleDetails() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const urlParams = new URLSearchParams(window.location.search);
  const bundleId = urlParams.get('id');
  const [selectedSampleToAdd, setSelectedSampleToAdd] = useState('');

  const { data: bundle, isLoading } = useQuery({
    queryKey: ['bundle', bundleId],
    queryFn: async () => {
      const bundles = await base44.entities.Bundle.filter({ id: bundleId });
      return bundles[0];
    },
    enabled: !!bundleId
  });

  const { data: bundleSamples = [] } = useQuery({
    queryKey: ['bundleSamples', bundleId],
    queryFn: () => base44.entities.Sample.filter({ bundle_id: bundleId }),
    enabled: !!bundleId
  });

  const { data: allSamples = [] } = useQuery({
    queryKey: ['samples'],
    queryFn: () => base44.entities.Sample.list()
  });

  // Samples not in this bundle
  const availableSamples = allSamples.filter(s => !s.bundle_id);

  const deleteMutation = useMutation({
    mutationFn: () => base44.entities.Bundle.delete(bundleId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['bundles'] });
      navigate(createPageUrl('Bundles'));
    }
  });

  const addSampleMutation = useMutation({
    mutationFn: (sampleId) => base44.entities.Sample.update(sampleId, { 
      bundle_id: bundleId,
      location: bundle.location // Update location to match bundle
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['bundleSamples', bundleId] });
      queryClient.invalidateQueries({ queryKey: ['samples'] });
      setSelectedSampleToAdd('');
    }
  });

  const removeSampleMutation = useMutation({
    mutationFn: (sampleId) => base44.entities.Sample.update(sampleId, { bundle_id: null }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['bundleSamples', bundleId] });
      queryClient.invalidateQueries({ queryKey: ['samples'] });
    }
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-slate-400" />
      </div>
    );
  }

  if (!bundle) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="w-12 h-12 text-amber-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-slate-900 mb-2">{t('bundle.notFound')}</h2>
          <Link to={createPageUrl('Bundles')}>
            <Button variant="outline">{t('actions.back')}</Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <Link to={createPageUrl('Bundles')} className="flex items-center gap-2 text-slate-600 hover:text-slate-900">
              <ArrowLeft className="w-4 h-4" />
              <span>{t('bundle.titlePlural')}</span>
            </Link>
            <div className="flex items-center gap-2">
              <Link to={createPageUrl(`BundleEdit?id=${bundle.id}`)}>
                <Button variant="outline" size="sm">
                  <Edit className="w-4 h-4 mr-2" />
                  {t('actions.edit')}
                </Button>
              </Link>
              <AlertDialog>
                <AlertDialogTrigger asChild>
                  <Button variant="outline" size="sm" className="text-red-600 hover:text-red-700 hover:bg-red-50">
                    <Trash2 className="w-4 h-4 mr-2" />
                    {t('actions.delete')}
                  </Button>
                </AlertDialogTrigger>
                <AlertDialogContent>
                  <AlertDialogHeader>
                    <AlertDialogTitle>{t('bundle.deleteBundle')}</AlertDialogTitle>
                    <AlertDialogDescription>
                      {t('bundle.deleteConfirm')} This will remove all samples from this bundle but won't delete the samples themselves.
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <AlertDialogFooter>
                    <AlertDialogCancel>{t('actions.cancel')}</AlertDialogCancel>
                    <AlertDialogAction 
                      onClick={() => deleteMutation.mutate()}
                      className="bg-red-600 hover:bg-red-700"
                    >
                      {t('actions.delete')}
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Bundle Info */}
        <Card className="mb-6">
          <CardContent className="p-6">
            <div className="flex items-start gap-6">
              <div className="w-20 h-20 rounded-2xl bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center flex-shrink-0">
                <Package className="w-10 h-10 text-white" />
              </div>
              <div className="flex-1">
                <h1 className="text-2xl font-bold text-slate-900 mb-2">{bundle.name}</h1>

                <div className="flex flex-wrap gap-4 mb-4">
                  {bundle.location && (
                    <div className="flex items-center gap-1 text-slate-600">
                      <MapPin className="w-4 h-4" />
                      {bundle.location}
                    </div>
                  )}
                  <span className="text-slate-500">{bundleSamples.length} samples</span>
                </div>

                <QRCodeDisplay code={bundle.qr_code} />

                {bundle.notes && (
                  <p className="mt-4 text-slate-600 p-4 bg-slate-50 rounded-lg">{bundle.notes}</p>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Samples Management */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>{t('bundle.samples')}</CardTitle>
            <div className="flex items-center gap-2">
              <Select value={selectedSampleToAdd} onValueChange={setSelectedSampleToAdd}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Select sample to add" />
                </SelectTrigger>
                <SelectContent>
                  {availableSamples.map(sample => (
                    <SelectItem key={sample.id} value={sample.id}>
                      {sample.name} ({sample.brand})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Button 
                onClick={() => addSampleMutation.mutate(selectedSampleToAdd)}
                disabled={!selectedSampleToAdd || addSampleMutation.isPending}
                size="sm"
              >
                <Plus className="w-4 h-4 mr-1" />
                {t('bundle.addSample')}
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {bundleSamples.length === 0 ? (
              <div className="text-center py-12">
                <Package className="w-12 h-12 text-slate-300 mx-auto mb-4" />
                <p className="text-slate-500">{t('bundle.noSamples')}</p>
                <p className="text-sm text-slate-400 mt-1">Add samples using the dropdown above</p>
              </div>
            ) : (
              <div className="space-y-3">
                {bundleSamples.map(sample => (
                  <div key={sample.id} className="flex items-center gap-3">
                    <div className="flex-1">
                      <Link to={createPageUrl(`SampleDetails?id=${sample.id}`)}>
                        <SampleCard sample={sample} compact />
                      </Link>
                    </div>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => removeSampleMutation.mutate(sample.id)}
                      disabled={removeSampleMutation.isPending}
                      className="text-slate-400 hover:text-red-500"
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

