components.ui/QRCodeDisplay:
import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Copy, Check, QrCode } from 'lucide-react';
import { useTranslation } from '../i18n/translations';

export default function QRCodeDisplay({ code, showImage = false }) {
  const [copied, setCopied] = useState(false);
  const { t } = useTranslation();

  const handleCopy = async () => {
    await navigator.clipboard.writeText(code);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="flex items-center gap-3">
      <div className="flex items-center gap-2 px-3 py-2 bg-slate-50 rounded-lg border border-slate-200 font-mono text-sm">
        <QrCode className="w-4 h-4 text-slate-400" />
        <span className="select-all">{code}</span>
      </div>
      <Button
        variant="ghost"
        size="icon"
        onClick={handleCopy}
        className="h-8 w-8"
      >
        {copied ? (
          <Check className="w-4 h-4 text-emerald-500" />
        ) : (
          <Copy className="w-4 h-4 text-slate-400" />
        )}
      </Button>
    </div>
  );
}

components.ui/PriceDisplay:
import React from 'react';
import { ExternalLink } from 'lucide-react';

export default function PriceDisplay({ currentPrice, bestPrice, bestPriceSource, lastChecked }) {
  const formatPrice = (price) => {
    if (price === null || price === undefined) return 'â€”';
    return `$${Number(price).toFixed(2)}`;
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return null;
    return new Date(dateStr).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  return (
    <div className="space-y-2">
      <div className="flex items-baseline gap-4">
        <div>
          <span className="text-sm text-slate-500">Current: </span>
          <span className="font-semibold text-lg">{formatPrice(currentPrice)}</span>
        </div>
        {bestPrice && (
          <div>
            <span className="text-sm text-slate-500">Best: </span>
            <span className="font-semibold text-emerald-600">{formatPrice(bestPrice)}</span>
          </div>
        )}
      </div>
      {bestPriceSource && (
        <a 
          href={bestPriceSource} 
          target="_blank" 
          rel="noopener noreferrer"
          className="inline-flex items-center gap-1 text-sm text-blue-600 hover:text-blue-800"
        >
          View source <ExternalLink className="w-3 h-3" />
        </a>
      )}
      {lastChecked && (
        <p className="text-xs text-slate-400">
          Last checked: {formatDate(lastChecked)}
        </p>
      )}
    </div>
  );
}

